    function checkForError(result) {
        var hasError = result.payload.error || result.payload.warning;
    
        if (hasError) {
            var key = '';
            var shouldDisableCTA = false;
            var hideFee = true;
    
            if (result.payload.error) {
                key = result.payload.error.key;
            }
            else {
                key = result.payload.warning.key;
            }
    
            var str = result.resourceStrings[key];
    
            switch (key.slice(key.lastIndexOf(".") + 1)) {
                case "manualpricing":
                    str = str.replace('%(sellCurrency)s', fromCCY).replace('%(buyCurrency)s', toCCY);
                    hideFee = false;
                    hasError = false;
                    break;
                case "currenciesnotsupported":
                    str = str.replace('%(sellCurrency)s', fromCCY).replace('%(buyCurrency)s', toCCY);
                    shouldDisableCTA = true;
                    break;
                case "belowminthreshold":
                    str = str.replace('%(minThreshold)s', convertToCurrentLocaleString(result.payload.warning.context.MIN, 2, 2, 'sdf'));
                    break;
                case "abovemaxthreshold":
                    str = str.replace('%(maxThreshold)s', convertToCurrentLocaleString(result.payload.warning.context.MAX, 2, 2, 'sdf'));
                    break;
                case "currenciesnottradable":
                    str = str.replace('%(currency)s', result.payload.error.context.CCY);
                    str = str.replace('%(startTime)s', result.payload.error.context.CCY_OPEN_TIME);
                    str = str.replace('%(endTime)s', result.payload.error.context.CCY_CLOSE_TIME);
                    shouldDisableCTA = true;
                    break;
    
                case "message":
                case "samecurrencyconversionnotallowed":
                    shouldDisableCTA = true;
                    break;
            }
            
            if (shouldDisableCTA)
                $('.optGreenCTA').addClass('disabled');
            if (hideFee)
                $('.optFeeAmount').text('-.--');
            //todo replace params
            $('.optErrorStep').html('<div class="transferconverter-errorBubble"><div class="transferconverter-arrowBox"></div><div class="transferconverter-errorMessage"><span>'+ str +'</span></div></div>');
            $('.optErrorStep').show();
    
            console.log(key);
            console.log(str);
        }
        else {
            $('.optGreenCTA').removeClass('disabled');
            $('.optErrorStep').empty();
            $('.optErrorStep').hide();
        }
    
        return hasError;
    }
