    //rates class
    function toLocaleStringSupportsLocales() {
        var number = 0;
    
        try {
            number.toLocaleString("i");
        } catch (event) {
            return event.name === "RangeError";
        }
        return false;
    }
    
    function toLocaleString(number, minimumFractionDigits, maximumFractionDigits, locale, useGrouping) {
        if (!toLocaleStringSupportsLocales()) {
            return customizedToLocaleString(number, minimumFractionDigits, maximumFractionDigits, locale, useGrouping);
        }
        return number.toLocaleString(locale, {
            minimumFractionDigits: minimumFractionDigits,
            maximumFractionDigits: maximumFractionDigits,
            useGrouping: useGrouping
        });
    }
    
    function customizedToLocaleString(number, minimumFractionDigits, maximumFractionDigits, locale, useGrouping) {
        var numberString = number.toString().indexOf("e") !== -1 ? Number.parseFloat(number).toFixed(20) : number.toString();
    
        if (parseFloat(numberString) === 0) {
            numberString = "0.0";   //The page looks so broken when the huge zero shows, so make it a float number
        }
    
        var isScientific = false;
        var numberArray = [];
        if (numberString.indexOf("e") !== -1) {
            isScientific = true;
            numberArray = numberString.split("e");
            numberString = numberArray[0];
        }
    
        var length = numberString.length;
        var digitPosition = numberString.indexOf(".") || numberString.indexOf(",");
        if (digitPosition !== -1) {       //Integer ignores this part
            var fractionDigits = length - digitPosition - 1;
    
            if (fractionDigits > maximumFractionDigits) {
                numberString = numberString.slice(0, maximumFractionDigits - fractionDigits);
                if (maximumFractionDigits === 0) {
                    numberString = numberString.slice(0, -1);
                }
            }
    
            if (fractionDigits < minimumFractionDigits) {
                var endingZeros = new Array(minimumFractionDigits - fractionDigits + 1).join("0");
                numberString = numberString.concat(endingZeros);
            }
        }
    
        if (isScientific && numberArray.length > 0) {
            numberString = numberString + "e" + numberArray[1];
        }
    
        var specialLocales = ["de", "es", "fr", "it", "pt", "sv"];
    
        if (specialLocales.indexOf(locale) !== -1) {
            numberString = numberString.replace(".", ",");
            numberString = useGrouping ? groupNumber(numberString, ",") : numberString;
        } else if (locale === "ar") {
            numberString = numberToArabic(numberString);
        } else {
            numberString = numberString.replace(",", ".");
            numberString = useGrouping ? groupNumber(numberString, ".") : numberString;
        }
    
        return numberString;
    }
    
    function groupNumber(numberString, dot) {
        var numberArray = numberString.split(dot);
        if (numberArray[0] && numberArray[0].length >= 4) {
            var splitter = dot === "." ? "$1," : "$1.";
            numberArray[0] = numberArray[0].replace(/(\d)(?=(?:\d{3})+$)/g, splitter);
        }   //only need to group the integer part as the prod does
    
        return numberArray.join(dot);
    }
    
    function numberToArabic(numberString) {
        var translatedStr = "";
        var i;
        var ch;
        var translatedCh;
        for (i = 0; i < numberString.length; i++) {
            ch = numberString.charCodeAt(i);
            if (ch >= 48 && ch <= 57) {
                // numerals
                translatedCh = ch + 1584;
            } else if (ch === 46) {
                // decimal
                translatedCh = 1643;
            } else if (ch === 69 || ch === 101) {
                // exponent 'E'
                translatedCh = 1587;
            } else {
                translatedCh = ch;
            }
            translatedStr += String.fromCharCode(translatedCh);
        }
        return translatedStr;
    }
    
    function decodeRatesData(data) {
        try {
            var strip4 = data.substr(data.length - 4);
            var codepos = strip4.charCodeAt(0) + strip4.charCodeAt(1) + strip4.charCodeAt(2) + strip4.charCodeAt(3);
            codepos = (data.length - 10) % codepos;
            codepos = (codepos > (data.length - 10 - 4)) ? (data.length - 10 - 4) : codepos;
    
            var key = data.substr(codepos, 10);
            data = data.substr(0, codepos) + data.substr(codepos + 10);
            var decodedData = decode64(decodeURIComponent(data));
    
            if (decodedData === false) {
                return false;
            }
    
            var result = "";
            var i = 0;
            var j = 0;
            for (i = 0; i < decodedData.length; i += 10) {
                var char1 = decodedData.charAt(i);
                var keychar = key.charAt(((j % key.length) - 1) < 0 ? (key.length + (j % key.length) - 1) : ((j % key.length) - 1));
                char1 = String.fromCharCode(char1.charCodeAt(0) - keychar.charCodeAt(0));
                result += (char1 + decodedData.substring(i + 1, i + 10));
                j++;
            }
            return result;
        } catch (e) {
            return false;
        }
    }
    
    function decode64(input) {
        try {
            var keyStr = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/=";
            var output = "";
            var chr1, chr2, chr3 = "";
            var enc1, enc2, enc3, enc4 = "";
            var i = 0;
    
            // remove all characters that are not A-Z, a-z, 0-9, +, /, or =
            var base64test = /[^A-Za-z0-9\+\/\=]/g;
            if (base64test.exec(input)) {
                return false;
            }
            input = input.replace(/[^A-Za-z0-9\+\/\=]/g, "");
    
            do {
                enc1 = keyStr.indexOf(input.charAt(i++));
                enc2 = keyStr.indexOf(input.charAt(i++));
                enc3 = keyStr.indexOf(input.charAt(i++));
                enc4 = keyStr.indexOf(input.charAt(i++));
    
                chr1 = (enc1 << 2) | (enc2 >> 4);
                chr2 = ((enc2 & 15) << 4) | (enc3 >> 2);
                chr3 = ((enc3 & 3) << 6) | enc4;
    
                output = output + String.fromCharCode(chr1);
    
                if (enc3 !== 64) {
                    output = output + String.fromCharCode(chr2);
                }
                if (enc4 !== 64) {
                    output = output + String.fromCharCode(chr3);
                }
    
                chr1 = chr2 = chr3 = "";
                enc1 = enc2 = enc3 = enc4 = "";
    
            } while (i < input.length);
    
            return unescape(output);
        } catch (e) {
            return false;
        }
    }
    
    function getUnitRate(fromCurrency, toCurrency, rates) {
        var fromBaseRate = rates[fromCurrency.toUpperCase()];
        var toBaseRate = rates[toCurrency.toUpperCase()];
        return {
            "rate": toBaseRate / fromBaseRate,
            "inverse": fromBaseRate / toBaseRate
        };
    }
    
    function getFormattedRate(rate, numDigits, fractionDigits, locale) {
        var formattedRate;
        var scientificFractionDigits = fractionDigits - (rate < 1 ? 3 : 2);
        locale = locale || "en";
    
        var localizedRate = toLocaleString(rate, fractionDigits, fractionDigits, locale, false);
    
        var englishRate = toLocaleString(rate, 1, fractionDigits, "en", false);
    
        if (localizedRate.length <= numDigits + fractionDigits - 1 && parseFloat(englishRate) !== 0) {
            formattedRate = toLocaleString(rate, numDigits + fractionDigits - localizedRate.length, numDigits + fractionDigits - localizedRate.length, locale, false);
        } else {
            formattedRate = getScientificNotation(rate, scientificFractionDigits);
            localizedRate = (parseFloat(localizedRate) === 0) ? rate.toFixed(15) : localizedRate;
            if (locale === "ar") {
                formattedRate = numberToArabic(formattedRate);
                localizedRate = numberToArabic(localizedRate);
            }
            formattedRate = [localizedRate, formattedRate];
        }
        return formattedRate;
    }
    
    function getRateDisplay(rate, locale) {
        return getFormattedRate(rate, 7, 5, locale);
    }
    
    function getScientificNotation(number, fractionDigits) {
        var formattedNumber = number.toExponential(fractionDigits).toUpperCase();
        if (number >= 1) {
            formattedNumber = formattedNumber.replace("+", "");
        }
        return formattedNumber;
    }
    
    
    function sigFigRound(val, sigFigs) {
        var x = Math.pow(10, Math.floor(Math.log10(val)) + 1 - sigFigs);
        return Math.floor(val / x + 0.5) * x;
    }
    
    function countSigFigs(number) {
        if (number === null) {
            return 0;
        }
    
        var str = number + "";
        var strIndex = 0;
        var sigFigs = 0;
        var ch = "";
        do {
            ch = str[strIndex++];
        } while ((strIndex < str.length) && (isNaN(+ch) || (ch === "0")));
    
        if (!isNaN(+ch)) {
            ++sigFigs;
        }
        while (strIndex < str.length) {
            ch = str[strIndex++];
            if (!isNaN(+ch)) {
                ++sigFigs;
            }
        }
        return sigFigs;
    }
    
    function parseRates(decodedRates) {
        return decodedRates ? JSON.parse(decodedRates) : null;
    }
    
    
    function removeInvalidCharacters(amount) {
        amount = amount.replace(/[^0-9\,\.]+/g, "");
        return amount;
    }
    
    function formatAmountToStandardizedNumber(amount, locale) {
        amount = removeInvalidCharacters(amount);
        amount = amount ? amount : "1";
    
        if (locale && typeof amount === "string") {
            if (decimalCommaLocales.indexOf(locale) >= 0) {
                amount = amount.replace(/\./g, "");
                amount = amount.replace(/\,/g, ".");
            } else {
                amount = amount.replace(/\,/g, "");
            }
        }
        return parseFloat(amount);
    }
    
    function removeDoubleDecimalZeros(amount, locale) {
        if (decimalCommaLocales.indexOf(locale) === -1 && typeof amount === "string") {
            return amount.endsWith(".00") ? amount.replace(".00", "") : amount;
        } else if (decimalCommaLocales.indexOf(locale) >= 0 && typeof amount === "string") {
            return amount.endsWith(",00") ? amount.replace(",00", "") : amount;
        }
    }
    
    function convertToCurrentLocaleString(amount, minFractionDigits, maxFractionDigits, locale) {
        amount = formatAmountToStandardizedNumber(amount.toString(), locale);
        amount = toLocaleString(amount, minFractionDigits, maxFractionDigits, locale, true);
        amount = amount === "NaN" ? "1" : amount;
        return removeDoubleDecimalZeros(amount, locale);
    }
    
